import React, { useEffect, useState } from 'react';
import todoService from '../services/TodoService';
import TodoEntity from '../models/TodoEntity';
import { useParams, useNavigate } from 'react-router-dom';
import { TextField, Checkbox, Button } from '@mui/material';

const TodoEdit = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [todo, setTodo] = useState < TodoEntity > (null);
    const [editedTitle, setEditedTitle] = useState('');
    const [editedDescription, setEditedDescription] = useState('');
    const [editedCompleted, setEditedCompleted] = useState(false);

    useEffect(() => {
        const fetchTodo = async () => {
            try {
                const fetchedTodo = await todoService.getTodoById(id);
                setTodo(fetchedTodo);
                setEditedTitle(fetchedTodo.title);
                setEditedDescription(fetchedTodo.description);
                setEditedCompleted(fetchedTodo.isCompleted);
            } catch (error) {
                console.error('Todo verisi çekilirken hata oluştu:', error);
            }
        };

        fetchTodo();
    }, [id]);

    useEffect(() => {
        const fetchTodo = async () => {
            try {
                const fetchedTodo = await todoService.getTodoById(id);
                setTodo(fetchedTodo);
                setEditedTitle(fetchedTodo.title);
                setEditedDescription(fetchedTodo.description);
                setEditedCompleted(fetchedTodo.isCompleted);
            } catch (error) {
                console.error('Todo verisi çekilirken hata oluştu:', error);
            }
        };

        fetchTodo();
    }, [id]);

    const handleSave = async () => {
        if (todo) {
            const updatedTodo: TodoEntity = {
                ...todo,
                title: editedTitle,
                description: editedDescription,
                isCompleted: editedCompleted,
            };

            await todoService.updateTodo(id, updatedTodo);
            navigate('/list');
        }
    };

    if (!todo) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Todo Düzenle: {todo.title}</h2>
            <TextField
                label="Başlık"
                value={editedTitle}
                onChange={(e) => setEditedTitle(e.target.value)}
            />
            <TextField
                label="Açıklama"
                value={editedDescription}
                onChange={(e) => setEditedDescription(e.target.value)}
            />
            <Checkbox
                checked={editedCompleted}
                onChange={(e) => setEditedCompleted(e.target.checked)}
            />
            <Button onClick={handleSave} variant="contained" color="primary">
                Kaydet
            </Button>
        </div>
    );
};

export default TodoEdit;
