import React, { useEffect, useState } from 'react';
import todoService from '../services/TodoService';
import TodoEntity from '../models/TodoEntity';
import { Checkbox, Dialog, DialogActions, DialogContent, Button } from '@mui/material';
import { DataGrid, GridColDef, GridValueGetterParams } from '@mui/x-data-grid';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import DeleteConfirmationDialog from '../components/DeleteConfirmationDialog';
import { useNavigate } from 'react-router-dom';

const TodoList = () => {
  const navigate = useNavigate();

  const [todos, setTodos] = useState([]);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [selectedTodoId, setSelectedTodoId] = useState(null);

  const fetchTodos = async () => {
    try {
      const fetchedTodos = await todoService.getTodos();
      setTodos(fetchedTodos);
    } catch (error) {
      console.error('Todo verileri çekilirken hata oluştu:', error);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const handleConfirmDelete = async () => {
    if (selectedTodoId) {
      await todoService.deleteTodo(selectedTodoId);
      await fetchTodos();
    }
    setSelectedTodoId(null);
    setDialogOpen(false);
  };

  const handleEdit = (id) => {
    navigate(`/edit/${id}`);
  };

  const handleCreateTodo = () => {
    navigate('/create');
  };

  const columns: GridColDef[] = [
    { field: 'id', headerName: 'ID', width: 70 },
    { field: 'title', headerName: 'Başlık', width: 250 },
    { field: 'description', headerName: 'Açıklama', width: 250 },
    {
      field: 'isCompleted',
      headerName: 'Tamamlandı',
      width: 120,
      renderCell: (params) => (
        <Checkbox disabled checked={params.value} />
      ),
    },
    {
      field: 'actions',
      headerName: 'İşlemler',
      width: 250,
      renderCell: (params) => (
        <Box>
          <IconButton color="primary">
            <EditIcon onClick={() => {
              handleEdit(params.row.id);
            }} />
          </IconButton>
          <IconButton
            color="secondary"
            onClick={() => {
              setSelectedTodoId(params.row.id);
              setDialogOpen(true);
            }}
          >
            <DeleteIcon />
          </IconButton>
        </Box>
      ),
    },
  ];

  return (
    <Box sx={{ height: 400, width: '100%' }}>
      <Box display="flex" justifyContent="flex-end" mb={2}></Box>
      <Button display="flex" justifyContent="" mb={2} variant="contained" color="primary" onClick={handleCreateTodo}>Ekle</Button>
      <DataGrid rows={todos} columns={columns} pageSize={5} disableSelectionOnClick />

      <DeleteConfirmationDialog
        open={dialogOpen}
        onClose={() => setDialogOpen(false)}
        onConfirm={handleConfirmDelete} />
    </Box>
  );
};

