    const columns: GridColDef[] = [
      { field: 'id', headerName: 'ID', width: 70 },
      { field: 'title', headerName: 'Başlık', width: 250 },
      {
        field: 'isCompleted',
        headerName: 'Tamamlandı',
        width: 120,
        renderCell: (params) => (
          <Button variant="outlined" color={params.value ? 'primary' : 'secondary'}>
            {params.value ? 'Evet' : 'Hayır'}
          </Button>
        ),
      },
      {
        field: 'actions',
        headerName: 'İşlemler',
        width: 150,
        renderCell: (params) => (
          <div>
            <Button variant="outlined" color="primary">
              Düzenle
            </Button>
            <Button variant="outlined" color="secondary" onClick={() => handleDelete(params.row.id)}>
              Sil
            </Button>
          </div>
        ),
      },
    ];

    return (
      <div style={{ height: 400, width: '100%' }}>
        <DataGrid rows={todos} columns={columns} pageSize={5} disableSelectionOnClick />
      </div>
