import React, { useEffect, useState } from 'react';
import todoService from '../services/TodoService';
import TodoEntity from '../models/TodoEntity';
import { Grid, Paper, List, ListItem, Checkbox, ListItemText, Button } from '@mui/material';

const TodoList = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {

    const fetchTodos = async () => {
      try {
        const fetchedTodos = await todoService.getTodos();
        setTodos(fetchedTodos);
      } catch (error) {
        console.error('Todo verileri çekilirken hata oluştu:', error);
      }
    };

    fetchTodos();
  }, []);

  const handleDelete = (id) => {
    // Silme işlemini gerçekleştirin
  };

  return (
    <div>
      <h1>Todo Listesi</h1>
      <Grid container spacing={2}>
        {todos.map((todo) => (
          <Grid item key={todo.id} xs={12} sm={6} md={4}>
            <Paper elevation={3} sx={{ padding: '16px' }}>
              <List>
                <ListItem dense>
                  <Checkbox
                    edge="start"
                    checked={todo.isCompleted}
                    tabIndex={-1}
                    disableRipple
                  />
                  <ListItemText
                    primary={todo.title}
                    secondary={todo.isCompleted ? 'Tamamlandı' : 'Tamamlanmadı'}
                  />
                </ListItem>
              </List>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '8px' }}>
                <Button variant="outlined" color="primary">
                  Düzenle
                </Button>
                <Button variant="outlined" color="secondary" onClick={() => handleDelete(todo.id)}>
                  Sil
                </Button>
              </div>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default TodoList;
